AccountSelect.prototype.init = function () {
    var self = this;
    for (var key in self.info) {
        var set = self.info[key];
        self.infoArray[set.code] = set;
        self[set.code] = set.id;
    }
    ;
};

AccountSelect.prototype.getInfoByCode = function (code) {
    var self = this;
    var obj = self.info;
    if (code != undefined) {
        obj = self.infoArray;
        obj = obj[code];
    }
    return obj;
};

module.exports = new AccountSelect();

