var CustomerStatus = function(){
    var self = this;
    self.info = [{id:0, code:'open', des:'正常'},
        {id:1, code:'close', des:'停用'}];
    self.infoArray = {};
    self.init();
};

CustomerStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

CustomerStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new CustomerStatus();

