var DigestType = function(){
    var self = this;
    self.info = [
        {id:0, code:'trippleDes', headCode:'3des', des:'3des-cfb-nopadding'},
        {id:1, code:'md5', headCode:'md5', des:'md5方式'}
    ];
    self.infoArray = {};
    self.init();
};

DigestType.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

DigestType.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new DigestType();

