var ErrUrl = function(){
    var self = this;
    self.info = [{id:0, code:'GUEST', url:'' , des:'游客'},
        {id:1, code:'CUSTOMER', url:'' , des:'普通用户'},
        {id:2, code:'CHANNEL', url:'sys_channelLogin.html' , des:'渠道用户'},
        {id:3, code:'ADMINISTRATOR', url:'sys_adminLogin.html' , des:'系统管理员'}];
    self.infoArray = {};
    self.init();
};

ErrUrl.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.url;
    };
};

ErrUrl.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new ErrUrl();

