var GameStatus = function(){
    var self = this;
    self.info=[{id:1000,code:"USABLE",des:"可售"},
        {id:2068,code:"NO_USABLE",des:"不可售"}];
    self.infoArray = {};
    self.init();
}

GameStatus.prototype.init = function(){
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
}

GameStatus.prototype.getInfoById = function(id){
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
}

module.exports = new GameStatus();