var MsgStatus = function(){
    var self = this;
    self.info = [{id:1000, code:'INIT', des:'初始状态'},
        {id:1500, code:'WAIT_CHILD', des:'等待子消息'},
        {id:2000, code:'HANDLING', des:'处理中'},
        {id:3000, code:'HANDLED', des:'已处理'},
        {id:3200, code:'FAILED', des:'处理失败'},
        {id:3500, code:'PRE_FINISH', des:'预完成'},
        {id:4000, code:'FINISHED', des:'已经完成'}];
    self.infoArray = {};
    self.init();
};

MsgStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

MsgStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new MsgStatus();

