var MsgType = function(){
    var self = this;
    self.info = [
        {id:1000, code:'TERM', des:'期次'},
        {id:1100, code:'TERM_HIT_FILE', des:'期次中奖文件'}
    ];
    self.infoArray = {};
    self.init();
};

MsgType.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

MsgType.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new MsgType();

