var NotifyStatus = function(){
    var self = this;
    self.info = [{id:0, code:'WAIT_TAKE', des:'等待取走'},
        {id:1, code:'TAKEN', des:'已经取走'}];
    self.infoArray = {};
    self.init();
};

NotifyStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

NotifyStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new NotifyStatus();

