var NotifyType = function(){
    var self = this;
    self.info = [{id:0, code:'TICKET', des:'票据'},
        {id:1, code:'TERM', des:'期次'},
        {id:2, code:'GAME', des:'游戏'}];
    self.infoArray = {};
    self.init();
};

NotifyType.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

NotifyType.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new NotifyType();

