var TermStatusForPage = function(){
    var self = this;
    self.info = [
        {id:1150, code:'PRE_ON_SALE', des:'准备开售中'},
        {id:1200, code:'ON_SALE', des:'正在销售'},
        {id:1300, code:'PAUSE', des:'销售暂停'},
        {id:1390, code:'PREEND', des:'销售结束中'},
        {id:1400, code:'END', des:'销售结束'},
        {id:1425, code:'PRE_SEND', des:'后台销售结束中'},
        {id:1450, code:'SEND', des:'后台销售结束'},
        {id:1490, code:'WAITING_DRAW_NUMBER', des:'等待录入开奖号码'},
        {id:1500, code:'DRAW', des:'已开奖'},
        {id:1600, code:'IN_CALCULATE', des:'算奖中'},
        {id:1601, code:'CALCULATE', des:'已算奖'},
        {id:1650, code:'IN_PAYOUT', des:'返奖中'},
        {id:1701, code:'PAYOUT', des:'已返奖'}];
    self.infoArray = {};
    self.init();
};

TermStatusForPage.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

TermStatusForPage.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new TermStatusForPage();

