var TicketPrintQueenStatus = function(){
    var self = this;
    self.info = [{id:1000, code:'WAITING_GET', des:'等待取走'},
        {id:1200, code:'TAKE_AWAY', des:'已经取走'},
        {id:1500, code:'PRINT_FAILURE', des:'出票失败'}];
    self.infoArray = {};
    self.init();
};

TicketPrintQueenStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    }
};

TicketPrintQueenStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new TicketPrintQueenStatus();

