var TicketPrintStatus = function(){
    var self = this;
    self.info = [{id:1100, code:'WAITING_PRINT', des:'等待打印'},
        {id:1300, code:'PRINT_SUCCESS', des:'出票成功'},
        {id:1500, code:'PRINT_FAILURE', des:'出票失败'}];
    self.infoArray = {};
    self.init();
};

TicketPrintStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    }
};

TicketPrintStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new TicketPrintStatus();

