var TicketStatus = function(){
    var self = this;
    self.info = [{id:1000, code:'UN_OPEN', des:'未开奖'},
        {id:1200, code:'HIT', des:'已中奖'},
        {id:1300, code:'NOT_HIT', des:'未中奖'},
        {id:1400, code:'REFUND', des:'已退款'}];
    self.infoArray = {};
    self.init();
};

TicketStatus.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    }
};

TicketStatus.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new TicketStatus();

