var UserType = function(){
    var self = this;
    self.info = [{id:0, code:'GUEST', des:'游客'},
        {id:1, code:'CUSTOMER', des:'普通用户'},
        {id:2, code:'CHANNEL', des:'渠道用户'},
        {id:3, code:'ADMINISTRATOR', des:'系统管理员'}];
    self.infoArray = {};
    self.init();
};

UserType.prototype.init = function()
{
    var self = this;
    for(var key in self.info)
    {
        var set = self.info[key];
        self.infoArray[set.id] = set;
        self[set.code] = set.id;
    };
};

UserType.prototype.getInfoById = function(id)
{
    var self = this;
    var obj = self.info;
    if(id != undefined)
    {
        obj = self.infoArray;
        obj = obj[id];
    }
    return obj;
};

module.exports = new UserType();

