var esut = require('easy_util');
var dc = require('mcp_db').dc;
var config = require('mcp_config');
var prop = config.prop;
var ec = config.ec;
var game = config.game;
var dateUtil = esut.dateUtil;
var digestUtil = esut.digestUtil;
var log = esut.log;
var pageUtil = esut.pageUtil;
var async = require('async');
var service = require("mcp_service");
var digestSer = service.digestSer;
var cons = require('mcp_constants');
var notifyStatus = cons.notifyStatus;
var msgType = cons.msgType;
var notifyType = cons.notifyType;
var userType = cons.userType;
var errUrl = cons.errUrl;

var NotifyPageControl = function(){};

NotifyPageControl.prototype.handle = function(headNode, bodyNode, cb)
{
    var self = this;
    var cmd = headNode.cmd;
    async.waterfall([
        //校验是否结束会话
        function (cb) {

            var userId = headNode.userId;
            if(userId == undefined)
            {
                if(headNode.userType!=undefined){
                    var userTypeId = userType[headNode.userType];
                    ec.E9006.url=errUrl.getInfoById(userTypeId).url;
                }
                cb(ec.E9006);
            }else{
                cb(null);
            }
        },
        //校验头的用户类型是否合法
        function(cb)
        {
            var userTypeId = userType[headNode.userType];
            if(userTypeId == undefined)
            {
                cb(ec.E9005);
            }
            else
            {
                cb(null, userTypeId);
            }
        },
        //获得密钥
        function(userTypeId, cb)
        {
            var fromType = prop.digestFromType.CACHE;
            digestSer.getKey({fromType:fromType, userId:headNode.userId, userType:userTypeId},
                function(err, key){
                    cb(err, key);
                });
        },
        //校验密钥并处理逻辑
        function(key, cb)
        {
            if(key != headNode.key)
            {
                cb(ec.E9003);
            }
            else
            {
                self[cmd[1]](headNode, bodyNode, cb);
            }
        }
    ], function (err, bodyNode) {
        cb(err, bodyNode);
    });
};

NotifyPageControl.prototype.list = function(headNode, bodyNode, cb)
{
    var self = this;
    var backBodyNode = {title:"view notifys"};
    pageUtil.parse(bodyNode, backBodyNode);
    var termTable = dc.mg.get("notifyqueen");
    var cursor = termTable.find(backBodyNode.cond, {}).sort(backBodyNode.sort).skip(backBodyNode.skip).limit(backBodyNode.limit);
    cursor.toArray(function(err, data){
        for(var key in data)
        {
            var set = data[key];
            set.status = notifyStatus.getInfoById(set.status);
            set.createTime = dateUtil.toString(set.createTime);
            set.takeTime = dateUtil.toString(set.takeTime);
            set.type = notifyType.getInfoById(set.type);

            if(set.type.id == notifyType.TICKET)
            {
                set.des = set.content.gameCode + ',' + set.content.id + ',' + set.content.status;
            }
            if(set.type.id == notifyType.TERM)
            {
                set.des = set.content.gameCode + ',' + set.content.code + ',' + set.content.status;
            }
        }
        backBodyNode.rst = data;
        backBodyNode.count = cursor.count(function(err, count){
            backBodyNode.count = count;
            cb(null, backBodyNode);
        });
    });
};


module.exports = new NotifyPageControl();