var feo = require("mcp_feo").feo;
var dlt = require("mcp_dlt").dlt;
var esf = require("mcp_esf").esf;
var kt = require("mcp_kt").kt;

var CheckFactory = function(){};

CheckFactory.prototype.getCheck = function(gameCode, gameGrade, wNum) {
    var self = this;
    var gls = [];
    for(var key in gameGrade)
    {
        gls[gls.length] = gameGrade[key].bonus;
    }
    if(gameCode == 'T06')
    {
        return self.getCheckT06(gls, wNum)
    }
    else if(gameCode == 'T01')
    {
        return self.getCheckT01(gls, wNum)
    }
    else if(gameCode == 'T05')
    {
        return self.getCheckT05(gls, wNum)
    }
    else if(gameCode == 'F04')
    {
        return self.getCheckF04(gls, wNum)
    }
    else
    {
        return null;
    }
}

CheckFactory.prototype.getCheckT06 = function(gls, wNum)
{
    var self = this;
    var gl = feo.gl(gls.length);
    gl.setBonus(gls);
    var check = feo.check();
    check.setDrawNum(wNum);
    check.setGl(gl);
    return check;
}

CheckFactory.prototype.getCheckT01 =  function(gls, wNum)
{
    var self = this;
    var gl = dlt.gl(gls.length);
    gl.setBonus(gls);
    var check = dlt.check();
    check.setDrawNum(wNum);
    check.setGl(gl);
    return check;
}

CheckFactory.prototype.getCheckT05 =  function(gls, wNum)
{
    var self = this;
    var gl = esf.gl(gls.length);
    gl.setBonus(gls);
    var check = esf.check();
    check.setDrawNum(wNum);
    check.setGl(gl);
    return check;
}

CheckFactory.prototype.getCheckF04 =  function(gls, wNum)
{
    var self = this;
    var gl = kt.gl(gls.length);
    gl.setBonus(gls);
    var check = kt.check();
    check.setDrawNum(wNum);
    check.setGl(gl);
    return check;
}


module.exports = new CheckFactory();