var cons = require('mcp_constants');
var termStatus = cons.termStatus;
var msgStatus = cons.msgStatus;
var msgType = cons.msgType;

var issue = require("mcp_issue");
var issueModel = issue.issueModel;

var job = require("mcp_job");

var ClientTermFileHandle = function(){};

ClientTermFileHandle.prototype.handle = function(msg, dTerm, cb)
{
    var self = this;
    async.waterfall([
        //处理消息
        function(cb)
        {
            if(dTerm.status == termStatus.IN_CALCULATE)
            {
                new job.CheckFileJob(dTerm).run(function(err, data){
                    cb(err, data);
                });
            }
            else
            {
                cb(null);
            }
        }
    ], function (err, result) {
        var cond = {_id:msg._id};
        var data = {$set:{
        }};
        var table = dc.mg_msg.get("msg");
        if(err)
        {
            data.$set.status = msgStatus.FAILED;
        }
        else
        {
            data.$set.status = msgStatus.HANDLED;
        }
        table.update(cond, data, [], function(err, data){
            cb(err, result);
        });
    });
}

module.exports = new ClientTermFileHandle();