/**
 * 期次开奖
 * @param cb
 */
TermService.prototype.draw = function(fromTerm, cb)
{
    var self = this;
    var table = dc.main.get("term");
    transaction.run(function(wCb){
        async.waterfall([
            //find term to open
            function(cb)
            {
                var cond = {id:fromTerm.id};
                var cols = {version:1, status:1, gameCode:1, code:1,
                    nextCode:1, openTime:1, closeTime:1, pool:1, wNum:1};
                table.findOne(cond, cols, [], function(err, data){
                    if(err || data == null )
                    {
                        log.error(err);
                        cb(ec.E0999);
                    }
                    else
                    {
                        cb(null, data);
                    }
                });
            },
            //校验期次状态
            function(term, cb)
            {
                //只要在销售结束中-等待录入开奖号码这段时间，都可以录入开奖号码
                if(term.status < termStatus.PREEND || term.status > termStatus.WAITING_DRAW_NUMBER)
                {
                    cb(ec.E2057);
                }
                else
                {
                    cb(null, term);
                }
            },
            //update term status
            function(term, cb)
            {
                var cond = {id:term.id, version:term.version};
                term.version++;
                var data = {$set:{version:term.version}};
                //如果传了开奖号码
                if(fromTerm.wNum)
                {
                    term.wNum = fromTerm.wNum;
                    data.$set.wNum = term.wNum;
                }
                //如果处于等待录入开奖号码状态，并且存在开奖号码
                if(term.status == termStatus.WAITING_DRAW_NUMBER && term.wNum)
                {
                    term.status = termStatus.DRAW;
                    data.$set.status = term.status;

                    term.bonusTime = new Date().getTime();
                    data.$set.bonusTime = term.bonusTime;
                }
                table.update(cond, data, {}, function(err, data){
                    if(err) {
                        log.error(err);
                        cb(ec.E0999);
                        return;
                    }
                    if(data.affectedRows == 1)
                    {
                        cb(ec.E0000, term);
                    }
                    else
                    {
                        cb(ec.E9999);
                    }
                });
            }
        ], function (err, result) {
            wCb(err, result);
        });
    }, function(err, data){
        cb(err, data);
    });
}

/**
 * 更新期次状态
 * @param cb
 */
TermService.prototype.updateStatusById = function(fromTerm, cb)
{
    var self = this;
    var table = dc.main.get("term");
    transaction.run(function(wCb){
        async.waterfall([
            //find term to handle
            function(cb)
            {
                var cond = {id:fromTerm.id};
                var cols = {version:1, status:1, gameCode:1, code:1,
                    nextCode:1, openTime:1, closeTime:1, pool:1, wNum:1};
                table.findOne(cond, cols, [], function(err, data){
                    if(err || data == null)
                    {
                        cb(ec.E0999);
                    }
                    else
                    {
                        cb(null, data);
                    }
                });
            },
            //update term status
            function(term, cb)
            {
                var cond = {id:term.id, version:term.version};
                term.status = fromTerm.status;
                term.version++;
                var data = {$set:{status:term.status, version:term.version}};
                table.update(cond, data, {}, function(err, data){
                    if(err)
                    {
                        cb(ec.E0999);
                    }
                    else
                    {
                        if(data.affectedRows == 1)
                        {
                            cb(ec.E0000, term);
                        }
                        else
                        {
                            cb(ec.E9999);
                        }
                    }
                });
            }
        ], function (err, result) {
            wCb(err, result);
        });
    }, function(err, data){
        cb(err, data);
    });
}

